import React from 'react'
import {useDispatch , useSelector} from 'react-redux'
import { useState, useEffect } from 'react'
import { loginUser } from '../actions/userActions'
function Loginscreen() {
    const [email, setemail] = useState('')
    const [password, setpassword] = useState('')
    const dispatch = useDispatch()
    function login(){
        const user = {email , password}
        dispatch(loginUser(us))
        
    }
    return (
        
        <div>
        <div className='row justify-content-center mt-5'>
            <div className='col-md-5 mt-5 text-left'>
                <h2 className='text-center m-2' style={{ fontSize: '35px' }}>Register</h2>
                <div>
                <input required type="text" placeholder='email' className='form-control' value={email} onChange={(e)=>{setemail(e.target.value)}} />
                        <input required type="text" placeholder='password' value={password} onChange={(e)=>{setpassword(e.target.value)}} className='form-control' />
                        <button onClick={login} className='btn mt-3 '>Login</button>

                </div>

            </div>
        </div>
    </div>
    )
}

export default Loginscreen
