import React from 'react'
import Stripecheckout from 'react-stripe-checkout'
function Checkout({subtotal}) {
    function tokenHandler(token)
    {
        console.log(token);
    }
    return (
        <div>
            <Stripecheckout
            amount={subtotal*100}
            shippingAddress
            token={tokenHandler}
            key=''
            currency='INR'
            >
                <button className='btn'>Pay Now</button>
            </Stripecheckout>
        </div>
    )
}

export default Checkout
