import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux';

function Adminscreen() {

    const userState = useSelector(state => state.loginUserReducer)
    const { currentUser } = userState
    const dispatch = useDispatch()
    useEffect(() => {

        if (!currentUser.isAdmin) {
            window.location.href = '/'
        }
    }, [])
    return (
        <div className="container1">
            <div className="navbar1">
                <div className="header">

                    <ul className="menu">

                        <li><a href="/login" className='color1'>users List</a></li>
                        <li><a href="/admin/orderlist" className='color1'>Pizzas List</a></li>
                        <li><a href="/admin/pizzaslist" className='color1'>Add New Pizza</a></li>
                        <li><a href="/admin/addpizza" className='color1'>Orders list</a></li>
                    </ul>

                </div>
            </div>

        </div>




    )
}

export default Adminscreen
