export const cartReducer=(state={cartItems : []} ,action)=>{
    switch (action.type)
    {
        case ' ADD_TO_CART' : 

            var temp = [...state.cartItems];                
            temp.push(action.payload);
            alert()
            return temp;
  

        default : return state

        
    }
}
